        public Collision(int lvlID)
        {
            LoadCollisionMap(lvlID);
        }

        public void LoadCollisionMap(int lvlID)
        {
            switch (lvlID)
            {
                case 1:
                    collisionMap = lvl1_CollisionMap;
                    break;
                case 2:
                    collisionMap = lvl2_CollisionMap;
                    break;
                case 3:
                    collisionMap = lvl3_CollisionMap;
                    break;
                case 4:
                    collisionMap = lvl4_CollisionMap;
                    break;
                default:
                    break;
            }
        }
        public void IsCollision()
        {
            for (int y = 0; y < tileMapHeight; y++)
            {
                for (int x = 0; x < tileMapWidth; x++)
                {
                    if (collisionMap[y, x] == 1)
                    {
                        rectangles.Add(new Rectangle(x * 64, y * 64, 64, 64));
                    }
                }
            }
        }
    }
