        public Map(int levelNumber)
        {
            switchLevel(levelNumber);
            LoadTextures();
        }

        void switchLevel(int levelnumber)
        {
            switch (levelnumber)
            {
                case 0:
                    Backlayer = lvl0_Backlayer;
                    MiddleLayer = lvl0_Middlelayer;
                    Forelayer = lvl0_Forelayer;
                    tileMapWidth = 50;
                    break;
                case 1:
                    Backlayer = lvl1_Backlayer;
                    MiddleLayer = lvl1_Middlelayer;
                    Forelayer = lvl1_Forelayer;
                    break;
                case 2:
                    Backlayer = lvl2_Backlayer;
                    MiddleLayer = lvl2_Middlelayer;
                    Forelayer = lvl2_Forelayer;
                    break;
                case 3:
                    Backlayer = lvl3_Backlayer;
                    MiddleLayer = lvl3_Middlelayer;
                    Forelayer = lvl3_Forelayer;
                    break;
                case 4:
                    Backlayer = lvl4_Backlayer;
                    MiddleLayer = lvl4_Middlelayer;
                    Forelayer = lvl4_Forelayer;
                    break;
                case 5:
                    Backlayer = lvl5_Backlayer;
                    MiddleLayer = lvl5_Middlelayer;
                    Forelayer = lvl5_Forelayer;
                    break;
                case 6:
                    Backlayer = lvl6_Backlayer;
                    MiddleLayer = lvl6_Middlelayer;
                    Forelayer = lvl6_Forelayer;
                    tileMapWidth = 50;
                    break;
                default:
                    break;
            }
        }

        /// <summary>
        /// Loads the needed textures
        /// </summary>
        public void LoadTextures()
        {
            TextureManager.StoreTexture();

        }

        /// <summary>
        /// Loads the array of the map.
        /// </summary>
        /// <param name="arr">Initialize the choosen map.</param>
        public void LoadArray(int[,] arr)
        {
            for (int x = 0; x < tileMapWidth; x++)
            {
                for (int y = 0; y < tileMapHeight; y++)
                {
                    map = arr;
                }
            }
        }
        /// <summary>
        /// Draws the wished map choosen by lvlID via switch.
        /// First: Drawing the background layer.
        /// Then: Drawing the foreground layer.
        /// </summary>
        /// <param name="spriteBatch"></param>
        /// <param name="lvlID">Choses a level-design</param>
        public void DrawMap(SpriteBatch spriteBatch)
        {
            LoadArray(Backlayer);
            LoadMap(spriteBatch, TextureManager.mapTileBacklayer);
            LoadArray(MiddleLayer);
            LoadMap(spriteBatch, TextureManager.mapTileMiddlelayer);
            LoadArray(Forelayer);
            LoadMap(spriteBatch, TextureManager.mapTileForelayer);
        }
        /// <summary>
        /// Draws the textures of the map.
        /// </summary>
        /// <param name="spriteBatch"></param>
        public void LoadMap(SpriteBatch spriteBatch, List<Texture2D> tile)
        {
            for (int x = 0; x < tileMapWidth; x++)
            {
                for (int y = 0; y < tileMapHeight; y++)
                {
                    int textureIndex = map[y, x];
                    Texture2D texture = tile[textureIndex];

                    spriteBatch.Draw(
                        texture,
                        new Rectangle(
                        x * tileWidth,
                        y * tileHeight,
                        tileWidth,
                        tileHeight),
                        Color.White);
                }
            }
        }

        Enemy antagonist_1;
        Enemy antagonist_2;

        public void setEnemies()
        {
            antagonists = new List<Enemy>() { };
            col = new Collision(OD.lvlID);
            col.IsCollision();

            if(OD.lvlID == 0)
            {
                antagonist_1 = new Enemy();
                antagonist_1.enemyinit(new Vector2(400, 720));
                antagonist_1.SetEntity(new Vector2(400, 720), 50, 46, "spritesheet-test2_1.png", null, 120, 1, 100, 10, 50, 0, 7, 2, col.rectangles);
                antagonists.Add(antagonist_1);
            }
            if (OD.lvlID == 1)
            {
                antagonist_2 = new Enemy();
                antagonist_2.enemyinit(new Vector2(600, 720));
                antagonist_2.SetEntity(new Vector2(600, 720), 50, 46, "spritesheet-test2_1.png", null, 120, 1, 100, 10, 50, 0, 7, 2, col.rectangles);
                antagonists.Add(antagonist_2);
            }
        }

        public void updateEnemies(GameTime gameTime, Player player)
        {
            if (OD.lvlID == 0)
            {
                if(antagonist_1.Hp>0)
                {
                    antagonist_1.Update(gameTime, 20, player, OD.lvlID);
                }
                
            }
            if (OD.lvlID == 1)
            {
                if (antagonist_2.Hp > 0)
                {
                    antagonist_2.Update(gameTime, 20, player, OD.lvlID);
                }
            }
        }

        public void drawEnemies(SpriteBatch spritebatch)
        {
            foreach (var antagonist in antagonists)
            {
                if (antagonist.Hp > 0)
                {
                    antagonist.Draw(spritebatch);
                }
            }
        }

